import React , {useState} from "react";


const UserInput =(props) =>{
    const [enteredUsername, setEnteredUsername] = useState("");
    const [enteredAge, setEnteredAge] = useState("");
    const [isValidUsername, setIsValidUsername] = useState(false);
    const [isValidAge, setIsValidAge] = useState(false);

    const addUser = (username,age)=>{
        console.log(username,age);

    }

    const inputUsernameChangeHandler = (event) =>{
        if(event.target.value.trim().lenght >0){
            setIsValidUsername(true);
        }
        setEnteredUsername(event.target.value);
        console.log(enteredUsername);
    }
    const inputAgeChangeHandler = (event) =>{
        if(event.target.value.trim().lenght >0&&isNaN(event.target.value)){
            setIsValidAge(true);
        }
        setEnteredAge(event.target.value);
    }
    const userSubmitHandler = (event) =>{
        event.preventDefault();
        if (enteredUsername.trim().length === 0){
            setIsValidUsername(false);
            console.log('wrong username');
            if(enteredAge < 0 && isNaN(enteredAge)){
                setIsValidAge(false);
                console.log('wrong age');
            }
            return
        }



        // props.onAddUser(enteredUsername,enteredAge);
    }




    return(
        <form onSubmit={userSubmitHandler}>
            <label>Username</label>
            <input type = "text" placeholder="Enter your username paskuda" onChange={inputUsernameChangeHandler} />
            <label>Age (in years)</label>
            <input type = "number" placeholder ="Enter your age padla" onChange={inputAgeChangeHandler}/>
            <button type = "submit" onClick={addUser}>Add User</button>
        </form>
    )

}

export default UserInput;
