import React, { useState } from "react";
import UserInput from "./UserInput";
import styled from "styled-components";
const UsersStyle = styled.div`
  margin: 0.5rem 0;
`;
const Users = (props) => {
  const [userRecords, setUserRecords] = useState([]);
  const onAddUser = (username, age, key) => {
    let recordsArray = userRecords;
    //recordsArray.unshift({username,age,id});
    console.log(recordsArray);
    setUserRecords([...recordsArray, ...[{ username, age, key }]]);

    console.log(userRecords);
  };
  return (
    <div>
      <UserInput getUsers={onAddUser} />
      <div
        className="[UsersStyle]"
        style={{ backgroundColor: "#81b71a", height: "200px" }}
      >
        {userRecords.map((r) => {
          return (
            <div>
              <div>{r.username}</div>
              <div>{r.age}</div>
              <div>{r.key}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Users;
