import React, { useState } from "react";
import UserInput from "./UserInput";
import UserItem from "./UserItem";
import styled from "styled-components";
const UsersStyle = styled.div`
  margin: 0.5rem 0;
`;
const Users = (props) => {
  const deleteItemHandler = (goalId) => {
    setCourseGoals((prevGoals) => {
      const updatedGoals = prevGoals.filter((goal) => goal.id !== goalId);
      return updatedGoals;
    });
  };
  const [userRecords, setUserRecords] = useState([]);
  const onAddUser = (username, age, id) => {
    let recordsArray = userRecords;
    setUserRecords([...recordsArray, ...[{ username, age, id }]]);
  };
  return (
    <div className="UsersStyle">
      <UserInput getUsers={onAddUser} />
      <ul style={{ backgroundColor: "#81b71a", height: "auto" }}>
        {userRecords.map((r) => {
          console.log(r);
          return (
            <UserItem
              userdata={r}
              masyanya="asf"
              key={r.id}
              id={r.id}
              onDelete={(id) => {
                console.log(id);
              }}
            >
              <span>{r.username + " "}</span>
              <span>{r.age}</span>
            </UserItem>
          );
        })}
      </ul>
      ;
    </div>
  );
};

export default Users;
