import React, { useState } from "react";
import UserInput from "./UserInput";
import UserItem from "./UserItem";
import styled from "styled-components";
import Card from "../UI/Card";
const UsersStyle = styled.div`
  margin: 0.5rem 0;
`;
const Users = (props) => {
  const [userRecords, setUserRecords] = useState([]);
  const onAddUser = (username, age, id) => {
    let recordsArray = userRecords;
    setUserRecords([...recordsArray, ...[{ username, age, id }]]);
  };
  return (
    <Card>
      <div className="UsersStyle">
        <UserInput getUsers={onAddUser} />
        <ul style={{ backgroundColor: "#81b71a", height: "auto" }}>
          {userRecords.map((record) => {
            console.log(record);
            return (
              <UserItem
                userdata={record}
                key={record.id}
                id={record.id}
                onDelete={(id) => {
                  console.log(id);
                }}
              >
                <span>{record.username + " "}</span>
                <span>{record.age}</span>
              </UserItem>
            );
          })}
        </ul>
      </div>
    </Card>
  );
};

export default Users;
